	<entry>
		<title>Страница автора</title>
		<link rel="alternate" type="text/html" href="http://www.lafamire.ru/index.php?option=com_content&amp;view=article&amp;id=1359:2011-05-13-11-20-53&amp;catid=10:2010-03-16-20-46-53&amp;Itemid=14"/>
		<published>2010-04-04T14:42:26Z</published>
		<updated>2010-04-04T14:42:26Z</updated>
